package Loop;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			    int Number = 6;
			    int facto=1;
			    int i=1;
			    
			    while(i<=Number){
			        facto*=i;
			        i++;
			   
			    }
			    
				System.out.println("Factorial of "+ Number + " is "+ facto);
			}
		
	}

